const c=o=>{const[s,a]=o.split(":");return Number(s)+Number(a)/60},p=({salarioBruto:o,jornadaMensal:s,horasExtras:a,horasExtrasNoturnas:r,horasExtrasDomingos:t})=>{const e=c(a),u=c(r),d=c(t),n=o/s,i=n*1.5*e,l=n*1.5*1.2*u,m=n*2*d,D=i+l+m;return{valorHorasExtras:i,valorHorasExtrasNoturnas:l,valorHorasExtrasDomingos:m,valorTotal:D}},x=o=>{let s=0;for(const a of o)if(a.entrada&&a.saida){const r=new Date(1970,1,1,Number(a.entrada.split(":")[0]),Number(a.entrada.split(":")[1]),1),t=new Date(1970,1,1,Number(a.saida.split(":")[0]),Number(a.saida.split(":")[1]),1);t<r&&t.setDate(t.getDate()+1);const e=t.getTime()-r.getTime();s+=e}return s};export{x as a,p as c};
